import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Button from '../components/Button';
import FormInput from '../components/FormInput';
import supabase from '../supabaseClient';
import { signInWithGoogle } from '../supabaseClient';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const emailInputRef = useRef(null);
  const passwordInputRef = useRef(null);
  const loginButtonRef = useRef(null); // Ref for the login button

  // Use useCallback to memoize handleLogin
  const handleLogin = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const { data, error: signInError } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (signInError) {
        setError(signInError.message);
      } else {
        console.log('Login successful:', data);
        navigate('/dashboard'); // Redirect to dashboard or any other page
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [email, password, navigate]); // Add dependencies to useCallback

  useEffect(() => {
    // Add event listener to the document to handle Enter key presses globally.
    const handleGlobalKeyDown = (e) => {
      if (e.key === 'Enter') {
        // Check if the focus is within the email or password input fields,
        // OR if the focus is on the login button itself.  This is the key change.
        if (
          document.activeElement === emailInputRef.current ||
          document.activeElement === passwordInputRef.current ||
          document.activeElement === loginButtonRef.current
        ) {
          e.preventDefault(); // Prevent any default form submission.
          handleLogin(); // Trigger the login.
        }
      }
    };

    document.addEventListener('keydown', handleGlobalKeyDown);

    // Cleanup the event listener when the component unmounts.
    return () => {
      document.removeEventListener('keydown', handleGlobalKeyDown);
    };
  }, [handleLogin]); //  Include handleLogin in the dependency array

  const handleSignInWithGoogle = async () => {
    setError(null);
    try {
      const { error: googleSignInError } = await signInWithGoogle();
      if (googleSignInError) {
        setError(googleSignInError.message);
      }
    } catch (err) {
      setError('Unexpected error occurred. Please try again.');
    }
  };


  return (
    <div>
      <h2>Log In</h2>
      <FormInput
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        ref={emailInputRef} // Attach the ref
      />
      <FormInput
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        ref={passwordInputRef} // Attach the ref
      />
      <Button
        onClick={handleLogin}
        disabled={loading}
        ref={loginButtonRef} // Attach ref to the login button
      >
        {loading ? 'Loading...' : 'Log In'}
      </Button>
      <button onClick={handleSignInWithGoogle} disabled={loading}>
        Log In with Google
      </button>
      {error && <p>{error}</p>}
      <p>
        Don't have an account? <Link to="/signup">Sign Up</Link>
      </p>
    </div>
  );
}

export default LoginPage;
