import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../App';
import { supabase } from '../supabaseClient';
import FormInput from '../components/FormInput'; // Adjust the path if necessary
import Button from '../components/Button'; // Adjust the path if necessary

const AccountPage = () => {
  const { session } = useAuth();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phone, setPhone] = useState('');
  const [loading, setLoading] = useState(true);
  const [updateLoading, setUpdateLoading] = useState(false);
  const [updateError, setUpdateError] = useState(null);
  const [updateSuccess, setUpdateSuccess] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (user) {
          setUser(user);
          setFirstName(user.user_metadata?.first_name || '');
          setLastName(user.user_metadata?.last_name || '');
          setPhone(user.user_metadata?.phone || '');
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };

    if (session?.user?.id) {
      fetchUserData();
    } else {
      setLoading(false);
    }
  }, [session?.user?.id]);

  const goToDashboard = () => {
    navigate('/dashboard');
  };

  const handleUpdateProfile = async () => {
    setUpdateLoading(true);
    setUpdateError(null);
    setUpdateSuccess(null);

    try {
      const { data, error } = await supabase.auth.updateUser({
        data: {
          first_name: firstName,
          last_name: lastName,
          phone: phone,
        },
      });

      if (error) {
        setUpdateError(error.message);
      } else {
        setUpdateSuccess('Profile updated successfully!');
        setUser({ ...user, user_metadata: { ...user.user_metadata, first_name: firstName, last_name: lastName, phone: phone } });
      }
    } catch (error) {
      setUpdateError(error.message);
    } finally {
      setUpdateLoading(false);
    }
  };

  if (!session) {
    return <div>Not authenticated. Redirecting...</div>;
  }

  if (loading) {
    return <div>Loading user data...</div>;
  }

  return (
    <div>
      <h1>My Account</h1>
      <p>User ID: {session?.user?.id}</p>

      <div>
        <h2>Update Profile</h2>
        <FormInput
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <FormInput
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <FormInput
          type="tel"
          placeholder="Phone Number (Optional)"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <Button onClick={handleUpdateProfile} disabled={updateLoading}>
          {updateLoading ? 'Updating...' : 'Update Profile'}
        </Button>
        {updateError && <p className="error">{updateError}</p>}
        {updateSuccess && <p className="success">{updateSuccess}</p>}
      </div>

      <button onClick={goToDashboard}>Back to Dashboard</button>
    </div>
  );
};

export default AccountPage;
