import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Button from '../components/Button';
import FormInput from '../components/FormInput';
import supabase from '../supabaseClient';
import { signInWithGoogle } from '../supabaseClient';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async () => {
    setLoading(true);
    setError(null);

    try {
      const { data, error: signInError } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (signInError) {
        setError(signInError.message);
      } else {
        console.log('Login successful:', data);
        navigate('/dashboard'); // Redirect to dashboard or any other page
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSignInWithGoogle = async () => {
    setError(null);
    try {
      const { error: googleSignInError } = await signInWithGoogle();
      if (googleSignInError) {
        setError(googleSignInError.message);
      }
    } catch (err) {
      setError('Unexpected error occurred. Please try again.');
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <div>
      <h2>Log In</h2>
      <FormInput
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        onKeyDown={handleKeyDown} // Added onKeyDown
      />
      <FormInput
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        onKeyDown={handleKeyDown} // Added onKeyDown
      />
      <Button onClick={handleLogin} disabled={loading}>
        {loading ? 'Loading...' : 'Log In'}
      </Button>
      <button onClick={handleSignInWithGoogle} disabled={loading}>
        Log In with Google
      </button>
      {error && <p>{error}</p>}
      <p>
        Don't have an account? <Link to="/signup">Sign Up</Link>
      </p>
    </div>
  );
}

export default LoginPage;
