import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate
import Button from '../components/Button';
import FormInput from '../components/FormInput';
import supabase from '../supabaseClient'; // Default export
import { signInWithGoogle } from '../supabaseClient'; // Named export

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // Initialize navigate

  const handleLogin = async () => {
    setLoading(true);
    setError(null);
    try {
      const { data, error: signInError } = await supabase.auth.signInWithPassword({ // Get data
        email,
        password,
      });

      if (signInError) {
        setError(signInError.message);
      } else {
        // Redirect to dashboard or another page on successful login
        navigate('/dashboard'); // Use navigate
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSignInWithGoogle = async () => {
    setError(null); // Reset error before starting
    try {
      const { error: googleSignInError } = await signInWithGoogle(); // Use the named export
      if (googleSignInError) {
        console.error('Error signing in with Google:', googleSignInError);
        setError(googleSignInError.message); // Set the error state
      } else {
        // Supabase handles redirect, no need to do anything here in most cases
        console.log('Redirecting to Google for sign-in...');
        // After successful Google sign-in, redirect.  Supabase should handle this, but
        //  we can add an explicit navigation here as well for belt-and-braces.
        navigate('/dashboard');
      }
    } catch (err) {
      setError("An unexpected error occurred");
      console.error("Unexpected error during Google sign-in", err);
    }
  };

  return (
    <div>
      <h2>Log In</h2>
      <FormInput
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <FormInput
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <Button onClick={handleLogin} disabled={loading}>
        {loading ? 'Logging in...' : 'Log In'}
      </Button>
      <button onClick={handleSignInWithGoogle}>Sign in with Google</button>
      {error && <p className="error">{error}</p>}
      <p>
        Don't have an account? <Link to="/signup">Sign Up</Link>
      </p>
    </div>
  );
}

export default LoginPage;
